import { textToDataUri, ContextMenuOptions, ContextMenuItemType, svgUriToPng } from './contextMenuUtils';

jest.mock('@joplin/lib/models/Resource');

describe('contextMenu', () => {
	it('should provide proper copy path', async () => {
		const testCases: Array<[ContextMenuOptions, string]> = [
			[
				{
					itemType: ContextMenuItemType.Image,
					resourceId: null,
					textToCopy: '<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" xml:space="preserve">test</svg>',
					mime: 'image/svg+xml',
					filename: 'Mermaid export.svg',
					linkToCopy: null,
					htmlToCopy: null,
					insertContent: () => { },
				},
				'data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHhtbG5zOnhsaW5rPSJodHRwOi8vd3d3LnczLm9yZy8xOTk5L3hsaW5rIiB4bWw6c3BhY2U9InByZXNlcnZlIj50ZXN0PC9zdmc+',
			],
		];

		for (const testCase of testCases) {
			const [inputObj, expectedText] = testCase;
			expect(textToDataUri(inputObj.textToCopy, inputObj.mime)).toBe(expectedText);
		}
	});

	it('should convert to png binary', async () => {
		const testCases: Array<string> = [
			'data:image/svg+xml;base64,',
		];

		for (const testCase of testCases) {
			const png = await svgUriToPng(document, testCase);
			expect(png).toBeInstanceOf(Uint8Array);
		}
	});

	it('should throw error on invalid svg uri', async () => {
		const testCases: Array<string> = [
			'data:image/svg+xml;base64,error',
			'invalid',
		];

		for (const testCase of testCases) {
			expect(svgUriToPng(document, testCase)).rejects.toBeInstanceOf(Error);
		}
	});
});
